package main

import (
	"flag"
	"fmt"
	"github.com/sirupsen/logrus"
	"jarvis_server/k8s"
	"jarvis_server/router"
)

//const (
//	Host     = "https://192.168.10.200:16443"
//	CAData   = ""
//	CertData = ""
//	KeyData  = ""
//)
const (
	Host     = "https://172.19.50.32:6443"
	CAData   = ""
	CertData = ""
	KeyData  = ""
)
func k8sInit() {
	instance, ok := k8s.DefaultManager.K8s("OriginalK8s")
	if !ok {
		fmt.Println(ok)
		return
	}
	instance.Init(Host, CAData, CertData, KeyData)
	_ = instance.KubeconfigInit()
}
func main() {
	verbose := flag.Bool("v", false, "verbose")
	flag.Usage = func() {
		fmt.Println("usage: server -h")
		flag.PrintDefaults()
	}
	flag.Parse()
	if *verbose {
		// 日志级别为Debug
		logrus.SetLevel(logrus.DebugLevel)
	} else {
		logrus.SetLevel(logrus.InfoLevel)
	}
	go k8sInit()
	router.InitRouter()
}
